import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap';
import CompanyFinder from '../../apis/CompanyFinder';
import '../../css/jobdetails.css'; // Import the CSS file
import 'bootstrap/dist/css/bootstrap.min.css';

const JobDetails = () => {
  const [jobs, setJobs] = useState([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await CompanyFinder.get('/User/Jobs');
        setJobs(response.data.data.jobs);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, [setJobs]);

  const handleApply = (jobId) => {
    // Add your logic for handling the apply button click, e.g., redirect to apply page or show a modal.
    console.log(`Applying for job with ID: ${jobId}`);
    setShowModal(true); // Show the modal on apply button click
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div>
      {jobs.map((job, index) => (
        <div key={index} className="job-details-box">
          <div className="job-details-content">
            <h3>{job.name}</h3>
            <p>
              <strong>Salary:</strong> {job.salary}
            </p>
            <p>
              <strong>Description:</strong> {job.description}
            </p>
            <p>
              <strong>Company:</strong> {job.company_name}
            </p>
          </div>
          <div className="job-details-buttons">
            <button onClick={() => handleApply(job.id)} className="apply-button">
              Apply
            </button>
          </div>
        </div>
      ))}

      {/* Bootstrap Modal */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Modal Title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Add your modal content here */}
          ...
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary">Save changes</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default JobDetails;
