import React, { useState } from 'react';

const SearchBar = () => {
  const [selectedOption, setSelectedOption] = useState('All');
  const [searchText, setSearchText] = useState('');

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleSearchTextChange = (event) => {
    setSearchText(event.target.value);
  };

  const handleSearch = () => {
    // Perform search based on selected option and search text
    console.log('Searching:', selectedOption, searchText);
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center' }}>
      <select value={selectedOption} onChange={handleOptionChange}>
        <option value="All">All</option>
        <option value="CategoryOne">Category One</option>
        <option value="CategoryTwo">Category Two</option>
        <option value="CategoryThree">Category Three</option>
      </select>

      <input
        type="text"
        value={searchText}
        onChange={handleSearchTextChange}
        placeholder="Search..."
        style={{ marginLeft: '10px' }}
      />

      <button type="button" onClick={handleSearch} style={{ marginLeft: '10px' }}>
        Search
      </button>
    </div>
  );
};

export default SearchComponent;
