import React, { useState } from 'react'
import CompnayFinder from '../../apis/CompanyFinder';
import { useParams } from 'react-router-dom'
//import { useContext } from 'react';
//import { CompanyContext } from '../../context/CompanyContext';

const AddCompany = () => {

    //const { addCompany } = useContext(CompanyContext);
    const { id } = useParams();
    const [company_id, setID] = useState("")
    const [name, setName] = useState("")
    const [salary, setSalary] = useState("")
    const [description, setDescription] = useState("")
    const [status,setStatus]  = useState ("")

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await CompnayFinder.post(`/Employer/${id}/addjob`, {
               
                name,
                salary,
                description,
                status
            });
            //addCompany(response.data.data.company);
            console.log(response);


        }
        catch (err) {
            console.log(err);
        }
    }

    return (
        <div className="mb-4">
            <form action="">
                <div className="form-row">
                    
                    <div className="col">
                        <input value={name} onChange={e => setName(e.target.value)} type="text" className="form-control" placeholder="Company Name" />
                    </div>
                    <div className="col">
                        <input value={salary} onChange={e => setSalary(e.target.value)} type="text" className="form-control" placeholder="Company Address" />
                    </div>
                    <div className="col">
                        <input value={description} onChange={e => setDescription(e.target.value)} type="text" className="form-control" placeholder="Company website" />
                    </div>
                    <div className="col">
                        <input value={status} onChange={e => setStatus(e.target.value)} type="text" className="form-control" placeholder="Company email" />
                    </div>
                    <button onClick={handleSubmit} type="submit" className="btn btn-primary">Add</button>
                </div>
            </form>
        </div>
    )
}

export default AddCompany
