import React, { useContext, useEffect ,useState} from 'react';
import { CompanyContext } from '../../context/CompanyContext';
import CompanyFinder from '../../apis/CompanyFinder';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import '../../css/companylist.css';
import SearchBar from './SearchBar';


const CompanyList = (props) => {
  const { company, setCompany } = useContext(CompanyContext);
  const navigate = useNavigate();
  const [filteredCompanies, setFilteredCompanies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await CompanyFinder.get("/User/Company");
       // setCompany(response.data.data.companies);
        setFilteredCompanies(response.data.data.companies); // Initialize with all companies
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, [setCompany]);

  const handleSearch = (selectedOption, searchText) => {
    // Implement your search logic here
    let filteredList = [...company];
    console.log (filteredList);
    console.log (selectedOption + " " + searchText);

    // if (selectedOption !== 'All') {
    //   // Filter by selected option
    //   filteredList = filteredList.filter((comp) => comp.category === selectedOption);
    // }

    // // Filter by search text
    // filteredList = filteredList.filter((comp) =>
    //   comp.name.toLowerCase().includes(searchText.toLowerCase())
    // );

   // setFilteredCompanies(filteredList);
  };

  return (
    <div>
      <h1>Company List</h1>
      <SearchBar options={[
          { value: 'All', label: 'All' },
          { value: 'Category One', label: 'Category One' },
          { value: 'Category Two', label: 'Category Two' },
          // Add more options as needed
        ]}
 onSearch={handleSearch} />

      <div className="company-list">
        {filteredCompanies.map((comp, index) => (
          <div key={index} className="smart-box">
            <h3>
              <Link to={`/company/${comp.company_id}`}>{comp.name}</Link>
            </h3>
            <p>
              <strong>Address:</strong> {comp.address}
            </p>
            <p>
              <strong>Website:</strong> {comp.website}
            </p>
            <p>
              <strong>Email:</strong> {comp.email}
            </p>
            <p>
              <strong>Total Jobs:</strong> {comp.total_jobs}
            </p>
            <p>
              <strong>Total Employees:</strong> {comp.total_employees}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CompanyList;
