// JobDetails.js

import React, { useState, useEffect } from 'react';
import CompanyFinder from '../../apis/CompanyFinder';
import '../../css/jobdetails.css'; // Import the CSS file

const JobDetails = () => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await CompanyFinder.get('/User/Jobs');
        setJobs(response.data.data.jobs);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, [setJobs]);

  const handleApply = (jobId) => {
    // Add your logic for handling the apply button click, e.g., redirect to apply page or show a modal.
    console.log(`Applying for job with ID: ${jobId}`);
  };

  const handleViewRequirements = (jobId) => {
    // Add your logic for handling the view requirements button click, e.g., redirect to requirements page or show a modal.
    console.log(`Viewing requirements for job with ID: ${jobId}`);
  };

  return (
    <div>
      {jobs.map((job, index) => (
        <div key={index} className="job-details-box">
          <div className="job-details-content">
            <h3>{job.name}</h3>
            <p>
              <strong>Salary:</strong> {job.salary}
            </p>
            <p>
              <strong>Description:</strong> {job.description}
            </p>
            <p>
              <strong>Company:</strong> {job.company_name}
            </p>
          </div>
          <div className="job-details-buttons">
          {/* <!-- Button trigger modal --> */}
<button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
  Launch demo modal
</button>

{/* <!-- Modal --> */}
<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
          {/* <button onClick={() => handleViewRequirements(job.id)} className="view-requirements-button">
              View Requirements
            </button> */}
            <button onClick={() => handleApply(job.id)} className="apply-button">
              Apply
            </button>
            
          </div>
        </div>
      ))}
    </div>
  );
};

export default JobDetails;
