import React, { useState } from 'react';

const SearchBar = ({ options =[], defaultOption, onSearch }) => {
  const [selectedOption, setSelectedOption] = useState(defaultOption || '');
  const [searchText, setSearchText] = useState('');

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleSearchTextChange = (event) => {
    setSearchText(event.target.value);
  };

  const handleSearch = () => {
    // Perform search based on selected option and search text
    onSearch(selectedOption, searchText);
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', padding: '20px' }}>
      <select
        value={selectedOption}
        onChange={handleOptionChange}
        style={{ fontSize: '20px', padding: '20px', marginRight: '10px' }}
      >
        {options.map((option) => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>

      <input
        type="text"
        value={searchText}
        onChange={handleSearchTextChange}
        placeholder="Search..."
        style={{ fontSize: '20px', padding: '10px', marginLeft: '10px' }}
      />

      <button
        type="button"
        onClick={handleSearch}
        style={{ fontSize: '20px', padding: '10px', marginLeft: '10px' }}
      >
        Search
      </button>
    </div>
  );
};

export default SearchBar;
