import React, { useState, useEffect } from 'react';
import CompanyFinder from '../../apis/CompanyFinder';
import '../../css/jobdetails.css'; // Import the CSS file

const JobDetails = () => {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await CompanyFinder.get('/User/Jobs');
        setJobs(response.data.data.jobs);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, [setJobs]);

  const handleApply = (jobId) => {
    // Add your logic for handling the apply button click, e.g., redirect to apply page or show a modal.
    console.log(`Applying for job with ID: ${jobId}`);
  };

  const handleViewRequirements = (jobId) => {
    // Set the selected job and open the modal
    setSelectedJob(jobId);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    // Close the modal and reset the selected job
    setSelectedJob(null);
    setIsModalOpen(false);
  };

  return (
    <div>
      {jobs.map((job, index) => (
        <div key={index} className="job-details-box">
          <div className="job-details-content">
            <h3>{job.name}</h3>
            <p>
              <strong>Salary:</strong> {job.salary}
            </p>
            <p>
              <strong>Description:</strong> {job.description}
            </p>
            <p>
              <strong>Company:</strong> {job.id}
            </p>
          </div>
          <div className="job-details-buttons">
            <button onClick={() => handleViewRequirements(job.job_id)} className="view-requirements-button">
              View Requirements
            </button>
            <button onClick={() => handleApply(job.id)} className="apply-button">
              Apply
            </button>
          </div>
        </div>
      ))}

      {isModalOpen && selectedJob && (
        // Modal structure for displaying job requirements
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h5 className="modal-title">Job Requirements</h5>
              <button type="button" className="close" onClick={handleCloseModal}>
                &times;
              </button>
            </div>
            <div className="modal-body">
              {/* Fetch and display job requirements or skills based on the selectedJob */}
              {/* You can fetch skills for the selected job and display them here */}
              {/* For simplicity, let's assume a static list of skills */}
              <ul>
                <li>Skill 1</li>
                <li>Skill 2</li>
                {/* Add more skills as needed */}
              </ul>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default JobDetails;
