import React, { useState } from "react";
import CompanyFinder from "../apis/CompanyFinder";
import { useNavigate, Link } from "react-router-dom";
//import "../css/loginPage.css"; // Add a CSS file for styling

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await CompanyFinder.post("/login", {
        email,
        password,
      });

      if (response.status === 200) {
        localStorage.setItem("token", response.data);
        console.log("Login successful!");
        navigate("/User/home");
      } else {
        console.log("Login failed!");
      }
    } catch (error) {
      console.error("Error during login:", error);
    }
  };

  return (
    <div className="container login-container">
      <div className="row justify-content-center mt-5">
        <div className="col-md-6">
          <form onSubmit={handleLoginSubmit} className="p-4 border rounded shadow">
            {/* Email input */}
            <div className="mb-4">
              <label htmlFor="form2Example1" className="form-label">
                Email address
              </label>
              <input
                type="email"
                id="form2Example1"
                className="form-control"
                value={email}
                onChange={handleEmailChange}
              />
            </div>

            {/* Password input */}
            <div className="mb-4">
              <label htmlFor="form2Example2" className="form-label">
                Password
              </label>
              <input
                type="password"
                id="form2Example2"
                className="form-control"
                value={password}
                onChange={handlePasswordChange}
              />
            </div>

            {/* Submit button */}
            <button type="submit" className="btn btn-primary btn-block">
              Sign in
            </button>

            {/* Signup link */}
            <p className="mt-3">
              Don't have an account? <Link to="/signup">Sign up</Link>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
