import React, { useState } from 'react'
import CompnayFinder from '../apis/CompnayFinder';

const AddCompany = () => {

    const [id,setId] = useState("")
    const [name,setName] = useState("")
    const [address,setAddress] = useState("")
    const [website,setWebsite] = useState("")
    const handleSubmit = async (e) => {
        e.preventDefault();
        try{
            const response = await CompnayFinder.post("/",{
                ID,
                Name,
                Location,
                website
            });
            console.log(response);
        }   
        catch(err){
            console.log(err);
        }
    }

  return (
    <div className="mb-4">
        <form action="">
            <div className="form-row">
            <div className="col">
                    <input value = {id} onChange = {e=>setId(e.target.value)} type="text" className="form-control" placeholder="Company ID"/>
                </div>
                <div className="col">
                    <input value = {name} onChange = {e=>setName(e.target.value)} type="text" className="form-control" placeholder="Company Name"/>
                </div>
                <div className="col">
                    <input value = {address} onChange = {e=>setAddress(e.target.value)} type="text" className="form-control" placeholder="Company Address"/>
                </div>
                <div className="col">
                    <input value = {website} onChange = {e=>setWebsite(e.target.value)} type="text" className="form-control" placeholder="Company website"/>
                </div>  
                <button onClick ={handleSubmit} type = "submit" className="btn btn-primary">Add</button>  
            </div>
        </form>
    </div>
  )
}

export default AddCompany
