import React, { useState, useEffect } from 'react';
import CompanyFinder from '../../apis/CompanyFinder';
import Modal from 'react-modal';
import '../../css/jobdetails.css'; // Import the CSS file

// Make sure to bind modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement('#root');

const JobDetails = () => {
  const [jobs, setJobs] = useState([]);
  const [modalIsOpen, setIsOpen] = useState(false);
  const [currentJob, setCurrentJob] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await CompanyFinder.get('/User/Jobs');
        setJobs(response.data.data.jobs);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, [setJobs]);

  const handleApply = (jobId) => {
    // Add your logic for handling the apply button click, e.g., redirect to apply page or show a modal.
    console.log(`Applying for job with ID: ${jobId}`);
  };

  const handleViewRequirements = (job) => {
    setCurrentJob(job);
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  return (
    <div>
      {jobs.map((job, index) => (
        <div key={index} className="job-details-box">
          <div className="job-details-content">
            <h3>{job.name}</h3>
            <p>
              <strong>Salary:</strong> {job.salary}
            </p>
            <p>
              <strong>Description:</strong> {job.description}
            </p>
            <p>
              <strong>Company:</strong> {job.company_name}
            </p>
          </div>
          <div className="job-details-buttons">
            <button onClick={() => handleViewRequirements(job)} className="view-requirements-button">
              View Requirements
            </button>
            <button onClick={() => handleApply(job.id)} className="apply-button">
              Apply
            </button>
          </div>
        </div>
      ))}
      <Modal
  isOpen={modalIsOpen}
  onRequestClose={closeModal}
  contentLabel="Job Requirements"
  style={{
    content: {
      width: '50%',  // Adjust the width as needed
      height: '50%', // Adjust the height as needed
      margin: 'auto',
      borderRadius: '5px',
      overflow: 'auto',
    },
    overlay: {
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
    },
  }}
>
  <h2>{currentJob?.name} Requirements</h2>
  <p>{currentJob?.requirements}</p>
  <button onClick={closeModal}>Close</button>
</Modal>

    </div>
  );
};

export default JobDetails;
