import React from 'react'
import { useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'

import Navbar from '../../components/company/Navbar'
import JobList from '../../components/company/JobList'
import { Link } from 'react-router-dom'


const Jobs = () => {

  const { id } = useParams()

  const [Name, setName] = useState([])

  useEffect(() => {
    const fetchData2 = async () => {
      try {
        const response = await CompanyFinder.get(`/Company/${id}`);
        setName(response.data.data.company.name);
        console.log(response.data.data.company.name);

      } catch (error) {

      }

    };
    fetchData2();
  }, [id])


  return (
    <div>
        <Navbar/>
        <JobList/>
        <h1>
        <Link to={`/Employer/${id}/addjob`}>
          Add a job
        </Link>
      </h1>
    </div>
  )
}

export default Jobs
