import React, { useContext, useEffect } from 'react';
import { CompanyContext } from '../../context/CompanyContext';
import CompanyFinder from '../../apis/CompanyFinder';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import '../'

const CompanyList = (props) => {
  const { company, setCompany } = useContext(CompanyContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await CompanyFinder.get("/User/Company");
        setCompany(response.data.data.companies);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, [setCompany]);

  return (
    <div className="list-group">
      <table className="table table-hover table-dark">
        <thead>
          <tr className="bg-primary">
            <th scope="col">Company</th>
            <th scope="col">Address</th>
            <th scope="col">Website</th>
            <th scope="col">Email </th>
            <th scope="col">Total jobs </th>
            <th scope="col">Total employees </th>
          </tr>
        </thead>
        <tbody>
          {company.map((comp, index) => (
            <tr key={index} className="smart-box">
              <td>
                <Link to={`/company/${comp.company_id}`}>{comp.name}</Link>
              </td>
              <td>{comp.address}</td>
              <td>{comp.website}</td>
              <td>{comp.email}</td>
              <td>{comp.total_jobs}</td>
              <td>{comp.total_employees}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CompanyList;
