import React, { useContext } from 'react'
import { useParams } from 'react-router-dom'
import { useState } from 'react';

import { CompanyContext } from '../context/CompanyContext';

const CompanyUpdate = (props) => {

  const {company}  = useContext(CompanyContext);
  const {id} = useParams();
  const [name,setName] = useState("");
  const [adrress,setAddress] = useState("");
  const [website,setWebsite] = useState("");

  return (
    <div>
      <h1>{company[0].ame}</h1>
      <form action="">
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input value ={name} onChange = {e => setName(e.target.value)} id="name" className="form-control" type="text" />
        </div>
        <div className="form-group">
          <label htmlFor="adrress">Adrress</label>
          <input value ={adrress} onChange = {e => setAddress(e.target.value)} id="adrress" className="form-control" type="text" />
        </div>
        <div className="form-group">
          <label htmlFor="website">URL</label>
          <input value ={website} onChange = {e => setWebsite(e.target.value)} id="website" className="form-control" type="text" />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  )
}

export default CompanyUpdate
